import React, { useState } from 'react';
import { useDisclosure } from '@chakra-ui/react';
import {
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
  Button,
  Input,
  Box,
  Flex,
} from '@chakra-ui/react';
import styles from './chatbot.module.css';

export default function Chatbot() {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = React.useRef();
  const [msg, setmsg] = useState('');

 async function showAddress(msg) {
    let obj={address:msg}
    const url = `http://127.0.0.1:5002/chatbot`;
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body:JSON.stringify(obj)
    };
    try {
        const response = await fetch(url, options);
        if (!response.ok) {
            alert('Request failed ');
          }

        const result = await response.json();
        alert(result.message);
      } catch (error) {
        alert(error);
      }

  }

  return (
    <div>
      <Button ref={btnRef} colorScheme='teal' onClick={onOpen}>
        Open
      </Button>
      <Drawer isOpen={isOpen} placement='right' onClose={onClose} finalFocusRef={btnRef}>
        <DrawerOverlay />
        <DrawerContent>
          <DrawerCloseButton />
          <DrawerHeader>
            Hello there! I am here to help guide you to tourist places. Please enter your Address.
          </DrawerHeader>

          <DrawerBody>
            <Input
              placeholder='Type here...'
              onChange={(e) => {
                setmsg(e.target.value);
              }}
            />
          </DrawerBody>
          <Box
            height="300px" // Set the fixed height of the container
            overflowY="scroll" // Enable vertical scrolling for overflowed content
            border="1px solid #ccc" // Optional: Add a border for better visualization
          >
            <Flex direction="column" p="4">
              {/* You can also use VStack instead of Flex */}
              <Box className={styles.box1} >
              Your Address : {msg}{/* Replace this with the actual content of your messages */}
              </Box>
              <Box className={styles.box2} >

              </Box>
              {/* Add more messages as needed */}
            </Flex>
          </Box>
          <DrawerFooter>
            <Button variant='outline' mr={3} onClick={onClose}>
              Close me!
            </Button>
            <Button colorScheme='blue' onClick={showAddress}>
              Send
            </Button>
          </DrawerFooter>
        </DrawerContent>
      </Drawer>
    </div>
  );
}
