import React, { useEffect, useState } from 'react';
import styles from "../pages/booking.module.css";
import { Divider, Center, Text, Card, CardBody, Stack, Heading, Flex, Image,CardFooter,Button } from '@chakra-ui/react';
import Navbar from "../pages/navbar";
import Footer from "../pages/footer";

export default function Booking() {

     const [arr,setarr]=useState([])

    useEffect(() => {
        async function fetchData() {
            try {
                let response = await fetch("http://127.0.0.1:5002/booking",{
                    method:"GET",
                    headers:{
                        'Content-Type': 'application/json'
                    }
                });
                if (!response.ok) {
                    throw new Error("Request failed");
                }
                const result = await response.json();
                console.log(result.message)
                let dt=result.message;
                let data= dt.map((item) => (
                    <Card key={item.id} maxW='sm'>
                      <CardBody className={styles.cardbody}>
                        <Image
                          src={item.images[0]}
                          alt='Hotel'
                          borderRadius='lg' className={styles.pht}
                        />
                        <Stack mt='6' spacing='3'>
                          <Heading size='md'>{item.name}</Heading>
                            <Text> No. of beds: {item.beds}</Text>
                           <Text> No. of bathrooms: {item.bathrooms}</Text>
                           <Text>Adress: {item.address}</Text>
                         <Flex justifyContent={"space-between"}>
                          <Text color='green.600' fontSize='2xl'>Price: ${item.price.total}</Text>
                          <Text color='green.600' fontSize='2xl'>Rating: {item.rating}</Text>
                          </Flex>
                        </Stack>
                      </CardBody>

                      <CardFooter>
                        <Button variant='solid' colorScheme='green' onClick={()=>
                        {
                        //    booking(item);
                        }}>
                          Book now
                        </Button>
                      </CardFooter>
                    </Card>

                  ));
                setarr(data);

            } catch (error) {
                console.error(error);
                console.log("Error occurred while fetching data");
            }
        }

        fetchData();
    }, []);

    return (
        <div>
            <Navbar />
            <Center height='100px'>
                <Divider />
            </Center>



            <Center height='100px'>
                <Divider />
            </Center>
            <Footer />
        </div>
    );
}
