import React, { useEffect, useState } from 'react';
import styles from "../pages/booking.module.css";
import { Divider, Center, Input, FormControl, FormLabel, Flex, Button, CardFooter, Text, CardHeader, Heading, Card, CardBody, SimpleGrid, ButtonGroup, Stack, Image, Select } from '@chakra-ui/react';
import Navbar from "../pages/navbar";
import Footer from "../pages/footer";
// import SectionPage from './SectionPage';

export default function Booking() {
    const [arr, setArr] = useState([]);

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await fetch("http://127.0.0.1:5002/bookHotel");
                if (!response.ok) {
                    throw new Error("Request failed");
                }
                const result = await response.json();
                setArr(result.message);
                console.log(result.message);
            } catch (error) {
                console.error(error);
                // Handle error here, show error message, etc.
            }
        }

        fetchData();
    }, []);

    return (
        <div>
            <Navbar />
            <Center height='100px'>
                <Divider />
            </Center>

            {/* Here, you can use the 'arr' state to render the data */}
            {arr.map((item) => (
        <Card key={item.id} maxW='sm'>
          <CardBody className={styles.cardbody}>
            <Image
              src={item.images[0]}
              alt='Hotel'
              borderRadius='lg' className={styles.pht}
            />
            <Stack mt='6' spacing='3'>
              <Heading size='md'>{item.name}</Heading>
                <Text> No. of beds: {item.beds}</Text>
               <Text> No. of bathrooms: {item.bathrooms}</Text>
               <Text>Adress: {item.address}</Text>
             <Flex justifyContent={"space-between"}>
              <Text color='green.600' fontSize='2xl'>Price: ${item.price.total}</Text>
              <Text color='green.600' fontSize='2xl'>Rating: {item.rating}</Text>
              </Flex>
            </Stack>
          </CardBody>

          <CardFooter>
            {/* <Button variant='solid' colorScheme='green' onClick={()=>
            {
               booking(item);
            }}>
              Book now
            </Button> */}
          </CardFooter>
        </Card>

      ))}

            <Center height='100px'>
                <Divider />
            </Center>
            <Footer />
        </div>
    );
}
