import React, { useEffect, useState } from 'react';
import styles from "../pages/booking.module.css";
import { Divider, Center, Text, Card, CardBody, Stack, Heading, Flex, Image } from '@chakra-ui/react';
import Navbar from "../pages/navbar";
import Footer from "../pages/footer";

export default function Booking() {
    const [arr, setArr] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await fetch("http://127.0.0.1:5002/bookHotel");
                if (!response.ok) {
                    throw new Error("Request failed");
                }
                const result = await response.json();
                console.log(result.message);
                setArr(result.message);
            } catch (error) {
                console.error(error);
                setError("Error occurred while fetching data");
            }
        }

        fetchData();
    }, []);

    return (
        <div>
            <Navbar />
            <Center height='100px'>
                <Divider />
            </Center>

            {/* Here, you can use the 'arr' state to render the data */}
            {error ? (
                <Center>
                    <Text>{error}</Text>
                </Center>
            ) : (
                Array.isArray(arr) && arr.length > 0 ? (
                    arr.map((item) => (
                        <Card key={item._id} maxW='sm'>
                            {/* ... rest of the code ... */}
                        </Card>
                    ))
                ) : (
                    <Center>
                        <Text>No data available</Text>
                    </Center>
                )
            )}

            <Center height='100px'>
                <Divider />
            </Center>
            <Footer />
        </div>
    );
}
