import React from 'react';
import { useDisclosure} from '@chakra-ui/react';
import { Drawer, DrawerBody, DrawerFooter, DrawerHeader, DrawerOverlay, DrawerContent, DrawerCloseButton, Button, Input,Box,Flex } from '@chakra-ui/react';
import styles from './chatbot.module.css';

export default function Chatbot() {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = React.useRef();
  const [msg,setmsg]=useState("")
    function showAddress()
    {
     document.getElementsByClassName("box1").innerText=msg;
    }
  return (
    <div>
      <Button ref={btnRef} colorScheme='teal' onClick={onOpen}>
        Open
      </Button>
      <Drawer
        isOpen={isOpen}
        placement='right'
        onClose={onClose}
        finalFocusRef={btnRef}
      >
        <DrawerOverlay />
        <DrawerContent>
          <DrawerCloseButton />
          <DrawerHeader>Hello there! I am here to help guide you to tourist places. Please enter your Address.</DrawerHeader>

          <DrawerBody>
            <Input placeholder='Type here...' onChange={(e)=>
            {
                setmsg(e.target.value);
            }} />
          </DrawerBody>
          <Box
      height="500px" // Set the fixed height of the container
      overflowY="scroll" // Enable vertical scrolling for overflowed content
      border="1px solid #ccc" // Optional: Add a border for better visualization
    >
      <Flex direction="column" p="4"> {/* You can also use VStack instead of Flex */}
        <Box className='box1'>
          Message 1 {/* Replace this with the actual content of your messages */}

        </Box>

        {/* Add more messages as needed */}
      </Flex>
    </Box>
          <DrawerFooter>
            <Button variant='outline' mr={3} onClick={onClose}>
              Close me!
            </Button>
            <Button colorScheme='blue' onClick={showAddress()}>Send</Button>
          </DrawerFooter>
        </DrawerContent>
      </Drawer>
    </div>
  );
}
