import React from 'react';
import { useDisclosure } from '@chakra-ui/react';
import { Drawer, DrawerBody, DrawerFooter, DrawerHeader, DrawerOverlay, DrawerContent, DrawerCloseButton, Button, Input,Container } from '@chakra-ui/react';
import styles from './chatbot.module.css';

export default function Chatbot() {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = React.useRef();

  return (
    <div>
      <Button ref={btnRef} colorScheme='teal' onClick={onOpen}>
        Open
      </Button>
      <Drawer
        isOpen={isOpen}
        placement='right'
        onClose={onClose}
        finalFocusRef={btnRef}
      >
        <DrawerOverlay />
        <DrawerContent>
          <DrawerCloseButton />
          <DrawerHeader>Hello there! I am here to help guide you to tourist places. Please enter your Address.</DrawerHeader>

          <DrawerBody>
            <Input placeholder='Type here...' />
          </DrawerBody>
          <Container border={"1px solid black"}>

</Container>
          <DrawerFooter>
            <Button variant='outline' mr={3} onClick={onClose}>
              Close me!
            </Button>
            <Button colorScheme='blue'>Send</Button>
          </DrawerFooter>
        </DrawerContent>
      </Drawer>
    </div>
  );
}
