import React from 'react';
import { useDisclosure } from '@chakra-ui/react';
import { Drawer, DrawerBody, DrawerFooter, DrawerHeader, DrawerOverlay, DrawerContent, DrawerCloseButton, Button, Input,Box,Slider,SliderTrack,SliderThumb } from '@chakra-ui/react';
import styles from './chatbot.module.css';

export default function Chatbot() {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = React.useRef();

  return (
    <div>
      <Button ref={btnRef} colorScheme='teal' onClick={onOpen}>
        Open
      </Button>
      <Drawer
        isOpen={isOpen}
        placement='right'
        onClose={onClose}
        finalFocusRef={btnRef}
      >
        <DrawerOverlay />
        <DrawerContent>
          <DrawerCloseButton />
          <DrawerHeader>Hello there! I am here to help guide you to tourist places. Please enter your Address.</DrawerHeader>

          <DrawerBody>
            <Input placeholder='Type here...' />
          </DrawerBody>
          <Box
      width="400px" // Set the fixed width of the container
      height="300px" // Set the fixed height of the container
      overflow="hidden" // Hide any content that overflows the container
    >
      <Slider // The Slider component acts as the carousel
        width="fit-content" // Make the slider as wide as its content
        display="flex" // Create a horizontal layout for the slides
        /* Optional: If you want the slides to move horizontally one by one */
        /* transition="transform 0.3s ease-in-out" */
      >
        <SliderTrack>
          <SliderFilledTrack />
        </SliderTrack>
        <SliderThumb display="none" />
        <Box flex="0 0 400px">Slide 1</Box> {/* Make each slide take the full width of the container */}
        <Box flex="0 0 400px">Slide 2</Box>
        <Box flex="0 0 400px">Slide 3</Box>
        {/* Add more slides as needed */}
      </Slider>
    </Box>
          <DrawerFooter>
            <Button variant='outline' mr={3} onClick={onClose}>
              Close me!
            </Button>
            <Button colorScheme='blue'>Send</Button>
          </DrawerFooter>
        </DrawerContent>
      </Drawer>
    </div>
  );
}
