import React, { useEffect, useState } from 'react';
import styles from "../pages/booking.module.css";
import { Flex, Spacer, Box, Divider, Center } from '@chakra-ui/react';
import Navbar from "../pages/navbar";
import Footer from "../pages/footer";
// import SectionPage from './SectionPage';

export default function Booking() {
    const [arr, setArr] = useState([]);

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await fetch("http://127.0.0.1:5002/bookHotel");
                if (!response.ok) {
                    throw new Error("Request failed");
                }
                const result = await response.json();
                setArr(result.message);
                console.log(result.message);
            } catch (error) {
                console.error(error);
                // Handle error here, show error message, etc.
            }
        }

        fetchData();
    }, []);

    return (
        <div>
            <Navbar />
            <Center height='100px'>
                <Divider />
            </Center>

            {/* Here, you can use the 'arr' state to render the data */}
            {arr.map((item) => (
                <Box key={item._id} p={5} shadow="md" borderWidth="1px">
                    <p>Name: {item.name}</p>
                    <p>Image: {item.image}</p>
                    <p>Bed: {item.bed}</p>
                    <p>Bathroom: {item.bathroom}</p>
                    <p>Price: {item.price}</p>
                    <p>Rating: {item.rating}</p>
                    <p>User ID: {item.userid}</p>
                    <p>Status: {item.status}</p>
                    <p>Address: {item.address}</p>
                </Box>
            ))}

            <Center height='100px'>
                <Divider />
            </Center>
            <Footer />
        </div>
    );
}
