import React from 'react'
import { Tabs, TabList, TabPanels, Tab, TabPanel } from '@chakra-ui/react'
import { Input,InputGroup,InputRightElement,Button, Divider, Center,Stack } from '@chakra-ui/react'
import Styles from "../pages/login-signup.module.css"

import Navbar from "../pages/navbar"
import Footer from "../pages/footer"
import { useState,useEffect } from "react";
import axios from "axios";

function register()
{
  let name=document.getElementById("rname").value;
  let email=document.getElementById("remail").value;
  let phone=document.getElementById("rnumber").value;
  let address=document.getElementById("raddress").value;

  axios({  
    method: 'post',  
    url: 'http://127.0.0.1:5002/register',  
    data: {  
      "name": name,
      "email": email,
      "password": hashed_password,
      "phone":phone,
      "address":address
    }  
  });  
}


export default function LoginSignup() {
    return (
      <div>
        <Navbar></Navbar>
            <Center height='100px'>
                <Divider />
            </Center>
      <div className={Styles.container}>

        <Tabs variant='soft-rounded' colorScheme='green'>
        <TabList>
          <Tab>LogIn</Tab>
          <Tab>SignUp</Tab>
        </TabList>
        <TabPanels>
          <TabPanel>
            <form>
              <Stack spacing={4}>

            <Input placeholder='Enter Email' size='lg' />
            {PasswordInput()}
            <Button colorScheme='green' size='lg' >Login</Button>
            </Stack>
            </form>
          </TabPanel>
          <TabPanel>
          <form>
              <Stack spacing={4}>
              <Input placeholder='Enter Name' size='lg' id="rname"/>
            <Input placeholder='Enter Email' size='lg' id="remail"/>
            {PasswordInput()}
            <Input placeholder='Enter Phone Number' type="number" size='lg'id="rnumber" />
            <Input placeholder='Enter Address'  size='lg' id="raddress"/>
            <Button colorScheme='green' size='lg' onClick={register()} >Signup</Button>

            </Stack>
            </form>
          </TabPanel>
        </TabPanels>
      </Tabs>

      </div>
      <Center height='100px'>
                <Divider />
            </Center>
            <Footer></Footer>
      </div>
    )
}
function PasswordInput() {
  const [show, setShow] = React.useState(false)
  const handleClick = () => setShow(!show)

  return (
    <InputGroup size='md'>
      <Input
        pr='4.5rem'
        type={show ? 'text' : 'password'}
        placeholder='Enter password' 
      />
      <InputRightElement width='4.5rem'>
        <Button h='1.75rem' size='sm' onClick={handleClick} >
          {show ? 'Hide' : 'Show'}

        </Button>
      </InputRightElement>
    </InputGroup>
  )
}
